import React, { useRef, useState } from "react";
import PageHeaderContent from "../../components/pageHeaderContent";
import { BsInfoCircleFill } from "react-icons/bs";
import { Animate } from "react-simple-animate";
import emailjs from "@emailjs/browser";
import "./style.scss";

const Contact = () => {
  const formRef = useRef(null);
  const [isSent, setIsSent] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "your_service_id", // Replace with your EmailJS Service ID
        "your_template_id", // Replace with your EmailJS Template ID
        formRef.current,
        "your_public_key" // Replace with your EmailJS Public Key
      )
      .then(
        (result) => {
          console.log("Success:", result.text);
          setIsSent(true);
          formRef.current.reset();
        },
        (error) => {
          console.log("Error:", error.text);
        }
      );
  };

  return (
    <section id="contact" className="contact">
      <PageHeaderContent
        headerText="Contact Me"
        icon={<BsInfoCircleFill size={40} />}
      />

      <div className="contact__content">
        {/* Animated Heading */}
        <Animate
          play
          duration={1}
          delay={0}
          start={{ transform: "translateX(-200px)", opacity: 0 }}
          end={{ transform: "translateX(0px)", opacity: 1 }}
        >
          <h3 className="contact__content__header-text">Let's Talk</h3>
        </Animate>

        {/* Contact Form */}
        <Animate
          play
          duration={1}
          delay={0.2}
          start={{ transform: "translateX(200px)", opacity: 0 }}
          end={{ transform: "translateX(0px)", opacity: 1 }}
        >
          <form ref={formRef} className="contact__content__form" onSubmit={sendEmail}>
            <div className="contact__content__form__controls-wrapper">
              <div className="input-group">
                <input required name="name" className="input-field" type="text" />
                <label className="input-label">Name</label>
              </div>

              <div className="input-group">
                <input required name="email" className="input-field" type="email" />
                <label className="input-label">Email</label>
              </div>

              <div className="input-group">
                <textarea required name="message" className="input-field" rows="5" />
                <label className="input-label">Message</label>
              </div>
            </div>

            <button type="submit" className="submit-btn">Send</button>
          </form>
          {isSent && <p className="success-message">Message sent successfully! âœ…</p>}
        </Animate>
      </div>
    </section>
  );
};

export default Contact;
